import React, { useState, useEffect } from "react";
import axios from "axios";

const getGreeting = () => {
  const apiUrl = "http://localhost:8282/";

  const GreetingData = [
    { id: 1, content: "Sin Contenido" },
  ];
  const [greeting, setGreeting] = useState(GreetingData);
  const [showLoading, setShowLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(apiUrl + "greeting");
      setGreeting(result.data.employeeList);
    };
    fetchData();
  }, []);




  const addEmployee = (employee) => {
    setShowLoading(true);
    const data = {
      id: employee.id,
      firstName: employee.firstName,
      lastName: employee.lastName,
      email: employee.email,
    };
    axios
      .post(apiUrl + "addemployee", data)
      .then((result) => {
        setEmployees([...employees, result.data]);
      })
      .catch((error) => setShowLoading(false));

    /*
    employee.id = employees.length + 1;
    setEmployees([...employees, employee]);

    const data = {
      id: parseInt(employee.id),
      firstName: employee.firstName,
      lastName: employee.lastName,
      email: employee.email,
    };

    axios
      .post(apiUrl, data)
      .then((result) => {
        //props.history.push('/show/' + result.data._id)
        console.log("Consumo del Servicio 2");
        console.log(result);
      })
      .catch((error) => setShowLoading(false));
*/
  };

  const deleteEmployee = (id) => {
    setEmployees(employees.filter((employee) => employee.id !== id));
    const data = {
      id: id,
      firstName: "",
      lastName: "",
      email: "",
    };
    axios
      .post(apiUrl + "employeeremove", data)
      .then((result) => {
        //console.log(result);
      })
      .catch((error) => setShowLoading(false));
  };

  const [editing, setEditing] = useState(false);

  const initialFormState = {
    id: null,
    firstName: "",
    lastName: "",
    email: "",
  };

  const [currentEmployee, setCurrentEmployee] = useState(initialFormState);

  const editRow = (employee) => {
    setEditing(true);
    setCurrentEmployee({
      id: employee.id,
      firstName: employee.firstName,
      lastName: employee.lastName,
      email: employee.email,
    });
  };

  const updateEmployee = (id, updatedEmployee) => {
    setEditing(false);
    setEmployees(
      employees.map((employee) =>
        employee.id === id ? updatedEmployee : employee
      )
    );
    const data = {
      id: updatedEmployee.id,
      firstName: updatedEmployee.firstName,
      lastName: updatedEmployee.lastName,
      email: updatedEmployee.email,
    };
    axios
      .post(apiUrl + "updateemployee", data)
      .then((result) => {
        //console.log("Updated");
      })
      .catch((error) => setShowLoading(false));
  };

  return (
    <div className="container">
      <h1>CRUD App with Hooks</h1>
      <div className="flex-row">
        <div className="flex-large">
          {editing ? (
            <div>
              <h2>Edit Employee</h2>
              <EditEmployeeForm
                setEditing={setEditing}
                currentEmployee={currentEmployee}
                updateEmployee={updateEmployee}
              />
            </div>
          ) : (
            <div>
              <h2>Add employee</h2>
              <AddEmployeeForm addEmployee={addEmployee} />
            </div>
          )}
        </div>

        <div className="flex-large">
          <h2>View Employees</h2>
          <EmployeeTable
            employees={employees}
            deleteEmployee={deleteEmployee}
            editRow={editRow}
          />
        </div>
      </div>
    </div>
  );
};

export default Employee;
